mapper.update:
update community 
set community.mu_c =  community.mu_c +1
where community.mu_id = #{mu_id}

update community
set mu_name = #{mu_name},
	mu_detail = #{mu_detail},
	ca_id =#{ca_id}
where community.mu_id = #{mu_id}

mapper.select:
SELECT * FROM dongnae.communityall
order by mu_data desc
limit #{displayPost}, #{postNum}

SELECT * FROM dongnae.communityall
where ca_l =#{ca_l}
order by mu_data desc
limit #{displayPost}, #{postNum}
